import React from "react";
export default function Usuario(){
    const [usuarioNovo, setUsuarioNovo] = React.useState(0);
    function alterarImg(){
        imgPerfil = prompt("Qual é o url da sua nova imagem de perfil?");
    }
    function alterarUsuario(){
        usuarioNovo = prompt("Qual será seu novo nome de usuário?");
    }
    return (
        <div>
            <div class="sidebar-usuario">
                <img src="Imagens/catanacomics.png" onclick="alterarImg()" alt=""/>
                <div class="infoU">
                    <p class="negrito">catanacomics</p>
                    <p class="nomeU">Catana <ion-icon name="pencil" onclick="alterarUsuario()"></ion-icon></p>
                </div>
            </div>
        </div>
    )
}
